import React, { useState, useMemo } from 'react';
import api from '../../services/api';
import './styles.css';
import camera from '../../assets/camera.svg';

export default function New({ history }) {
  const [company, setCompany] = useState('');
  const [techs, setTechs] = useState('');
  const [price, setPrice] = useState('');
  const [thumbnail, setThumbnail] = useState(null);

  const preview = useMemo(() => {
    return thumbnail ? URL.createObjectURL(thumbnail) : null;
  }, [thumbnail]);

  async function handleSubmit(e) {
    e.preventDefault();
    const user_id = localStorage.getItem('user');

    const formData = new FormData();

    formData.append('thumbnail', thumbnail);
    formData.append('company', company);
    formData.append('techs', techs);
    formData.append('price', price);

    await api.post('/spots', formData, {
      headers: { user_id }
    });

    history.push('/dashboard');
  }

  return (
    <form onSubmit={handleSubmit}>
      <label id="thumbnail" style={{ backgroundImage: `url(${preview})` }} className={thumbnail ? 'has-thumbnail' : ''}>
        <input type="file" onChange={event => setThumbnail(event.target.files[0])} />
        <img src={camera} alt="Select img" />
      </label>

      <label htmlFor="company">EMPRESA *</label>
      <input id="company" type="text" placeholder="Sua empresa incrível" onChange={event => setCompany(event.target.value)} />

      <label htmlFor="techs">TECNOLOGIAS * <span>(separadas por virgula)</span></label>
      <input id="techs" type="text" placeholder="Quais tecnologias usam?" onChange={event => setTechs(event.target.value)} />

      <label htmlFor="price">VALOR DA DIÁRIA * <span>(em branco para grauito)</span></label>
      <input id="price" type="text" placeholder="Valor cobrado por dia" onChange={event => setPrice(event.target.value)} />

      <button className="btn" type="submit">Cadastrar</button>
    </form>
  )
}
